    output logic heater,        // Output to control the heater
    output logic aircon,        // Output to control the air conditioning
    output logic fan            // Output to control the circulation fan
);

always @(*) begin
    // Default outputs
    heater = 0;
    aircon = 0;
    fan = 0;

    // Heating mode
    if (mode) begin
        if (too_cold) begin
            heater = 1;
        end
    end
    // Cooling mode
    else begin
        if (too_hot) begin
            aircon = 1;
        end
    end

    // Fan control
    fan = heater || aircon || fan_on;
end

endmodule

