    logic [99:0] in;
    logic [99:0] out_both;
    logic [99:0] out_any;
    logic [99:0] out_different;

    // Stats for tracking errors
      int errors;
    // Instantiate the Device Under Test (DUT)
    TopModule dut (
        .in(in),
        .out_both(out_both),
        .out_any(out_any),
        .out_different(out_different)
    );

    // Test stimulus
    initial begin
        // Initialize inputs
        in = 100'b0;

        // Wait for reset
        #10;

        // Apply input stimulus
        in = 100'b0;
        #10;

        // Check output
        if (out_both !== 100'b0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 100'b0);
            $display("Time %0t: Output %s = %h", $time, "out_both", out_both);
            errors++;
        end
        if (out_any !== 100'b0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 100'b0);
            $display("Time %0t: Output %s = %h", $time, "out_any", out_any);
            errors++;
        end
        if (out_different !== 100'b0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 100'b0);
            $display("Time %0t: Output %s = %h", $time, "out_different", out_different);
