testbench_results.testbench:
`timescale 1ns/1ps

module TopModule_tb;

    logic [3:0] in;
    logic [1:0] pos;

    // Stats for tracking errors
      int errors;
    // Instantiate the Device Under Test (DUT)
    TopModule dut (
        .in(in),
        .pos(pos)
    );

    // Test stimulus
    initial begin
        // Initialize inputs
        in = 4'b0;

        // Wait for reset
        #10;

        // Apply input stimulus
        in = 4'b0000;
        #10;

        // Check output
        if (pos !== 2'b00) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 4'b0000);
            $display("Time %0t: Output %s = %h", $time, "pos", pos);
            errors++;
        end

        // Apply input stimulus
        in = 4'b0001;
        #10;

        // Check output
        if (pos !== 2'b00) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 4'b0001);
            $display("Time %0t: Output %s = %h", $time, "pos", pos);
            errors++;
        end

        // Apply input stimulus
        in = 4'b0010;
        #10;

        // Check output
        if (pos !== 2'b01) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 4'b0010);
            $display("Time %0t: Output %s = %h", $time, "pos", pos);
            errors++;
        end

        // Apply input stimulus
        in = 4'b0100;
        #10;

        // Check output
        if (pos !== 2'b10) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 4'b0100);
            $display("Time %0t: Output %s = %h", $time, "pos", pos);
            errors++;
        end

        // Apply input stimulus
        in = 4'b1000;
        #10;

        // Check output
        if (pos !== 2'b11) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 4'b1000);
            $display("Time %0t: Output %s = %h", $time, "pos", pos);
            errors++;
        end

        // Apply input stimulus
        in = 4'b0011;
        #10;

        // Check output
        if (pos !== 2'b01) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 4'b0011);
            $display("Time %0t: Output %s = %h", $time, "pos", pos);
            errors++;
        end

        // Apply input stimulus
        in = 4'b0110;
        #10;

        // Check output
        if (pos !== 2'b10) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 4'b0110);
            $display("Time %0t: Output %s = %h", $time, "pos", pos);
            errors++;
        end

        // Apply input stimulus
        in = 4'b1100;
        #10;

        // Check output
        if (pos !== 2'b11) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 4'b1100);
            $display("Time %0t: Output %s = %h", $time, "pos", pos);
            errors++;
        end

        // Apply input stimulus
        in = 4'b0111;
        #10;

        // Check output
        if (pos !== 2'b01) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 4'b0111);
            $display("Time %0t: Output %s = %h", $time, "pos", pos);
            errors++;
        end

        // Apply input stimulus
        in = 4'b1110;
        #10;

        // Check output
        if (pos !== 2'b11) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 4'b1110);
            $display("Time %0t: Output %s = %h", $time, "pos", pos);
            errors++;
        end

        // Apply input stimulus
        in = 4'b1111;
        #10;

        // Check output
        if (pos !== 2'b00) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 4'b1111);
            $display("Time %0t: Output %s = %h", $time, "pos", pos);
            errors++;
        end
    end

    // Final reporting
    final begin
        $display("Simulation finished at %0d ps", $time);
        if (errors == 0)
            $display("TEST PASSED");
        else
            $display("TEST FAILED with %0d errors", errors);
    end

endmodule
