testbench_results.testbench:
`timescale 1ns/1ps

module TopModule_tb;

    logic [7:0] in;
    logic [31:0] out;

    // Stats for tracking errors
      int errors;
    // Instantiate the Device Under Test (DUT)
    TopModule dut (
        .in(in),
        .out(out)
    );

    // Test stimulus
    initial begin
        // Initialize inputs
        in = 8'b0;

        // Wait for reset
        #10;

        // Apply input stimulus
        in = 00000000;
        #10;

        // Check output
        if (out !== 00000000000000000000000000000000) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 00000000);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 00000001;
        #10;

        // Check output
        if (out !== 00000000000000000000000000000001) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 00000001);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 01111111;
        #10;

        // Check output
        if (out !== 00000000000000000000000001111111) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 01111111);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 10000000;
        #10;

        // Check output
        if (out !== 11111111111111111111111110000000) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 10000000);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 11111111;
        #10;

        // Check output
        if (out !== 11111111111111111111111111111111) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 11111111);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 01010101;
        #10;

        // Check output
        if (out !== 00000000000000000000000001010101) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 01010101);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 00110011;
        #10;

        // Check output
        if (out !== 00000000000000000000000000110011) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 00110011);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 11001100;
        #10;

        // Check output
        if (out !== 11111111111111111111111111001100) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 11001100);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 10101010;
        #10;

        // Check output
        if (out !== 11111111111111111111111110101010) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 10101010);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 00001111;
        #10;

        // Check output
        if (out !== 00000000000000000000000000001111) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 00001111);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 11110000;
        #10;

        // Check output
        if (out !== 11111111111111111111111111110000) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 11110000);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end
    end

    // Final reporting
    final begin
        $display("Simulation finished at %0d ps", $time);
        if (errors == 0)
            $display("TEST PASSED");
        else
            $display("TEST FAILED with %0d errors", errors);
    end

endmodule
