module TopModule (
    input logic clk,
    input logic a,
    input logic b,
    output logic q,
    output logic state
);

    logic ff_state;

    always @(posedge clk) begin
        if (a && !b) begin
            ff_state <= 1'b1;
        end else if (!a && b) begin
            ff_state <= 1'b0;
        end
    end

    assign state = ff_state;
    assign q = ff_state;

endmodule
