testbench_results.testbench:
`timescale 1ns/1ps

module TopModule_tb;

    logic [15:0] in;
    logic [7:0] out_hi;
    logic [7:0] out_lo;

    // Stats for tracking errors
      int errors;
    // Instantiate the Device Under Test (DUT)
    TopModule dut (
        .in(in),
        .out_hi(out_hi),
        .out_lo(out_lo)
    );

    // Test stimulus
    initial begin
        // Initialize inputs
        in = 16'b0;

        // Wait for reset
        #10;

        // Apply input stimulus
        in = 16'b0000000000000000;
        #10;

        // Check output
        if (out_hi !== 8'b00000000) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 16'b0000000000000000);
            $display("Time %0t: Output %s = %h", $time, "out_hi", out_hi);
            errors++;
        end
        if (out_lo !== 8'b00000000) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 16'b0000000000000000);
            $display("Time %0t: Output %s = %h", $time, "out_lo", out_lo);
            errors++;
        end

        // Apply input stimulus
        in = 16'b0000000000000001;
        #10;

        // Check output
        if (out_hi !== 8'b00000000) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 16'b0000000000000001);
            $display("Time %0t: Output %s = %h", $time, "out_hi", out_hi);
            errors++;
        end
        if (out_lo !== 8'b00000001) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 16'b0000000000000001);
            $display("Time %0t: Output %s = %h", $time, "out_lo", out_lo);
            errors++;
        end

        // Apply input stimulus
        in = 16'b0000000000000010;
        #10;

