        // Check output
        if (out_hi !== 8'b11001100) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 16'b1100110011001100);
            $display("Time %0t: Output %s = %h", $time, "out_hi", out_hi);
