            $display("            %s = %h", "in", 16'b0001001000010010);
            $display("Time %0t: Output %s = %h", $time, "out_lo", out_lo);
            errors++;
        end
    end

    // Final reporting
    final begin
        $display("Simulation finished at %0d ps", $time);
        if (errors == 0)
            $display("TEST PASSED");
        else
            $display("TEST FAILED with %0d errors", errors);
