    // Instantiate the Device Under Test (DUT)
    TopModule dut (
        .a(a),
        .b(b),
        .sel(sel),
        .out(out)
    );

    // Test stimulus
    initial begin
        // Initialize inputs
        a = 1'b0;
        b = 1'b0;
        sel = 1'b0;

        // Wait for reset
        #10;

        // Apply input stimulus
        a = 0;
        b = 0;
        sel = 0;
        #10;

        // Check output
        if (out !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 0);
            $display("            %s = %h", "b", 0);
            $display("            %s = %h", "sel", 0);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

