`timescale 1ns/1ps

module TopModule_tb;

    logic  sel;
    logic [7:0] a;
    logic [7:0] b;
    logic [7:0] out;

    // Stats for tracking errors
      int errors;
    // Instantiate the Device Under Test (DUT)
    TopModule dut (
        .sel(sel),
        .a(a),
        .b(b),
        .out(out)
    );

    // Test stimulus
    initial begin
        // Initialize inputs
        sel = 1'b0;
        a = 8'b0;
        b = 8'b0;

        // Wait for reset
        #10;

        // Apply input stimulus
        sel = 0;
        a = 8'b00000000;
        b = 8'b11111111;
        #10;

        // Check output
        if (out !== 8'b00000000) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "sel", 0);
            $display("            %s = %h", "a", 8'b00000000);
            $display("            %s = %h", "b", 8'b11111111);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        sel = 0;
        a = 8'b11111111;
        b = 8'b00000000;
        #10;

        // Check output
        if (out !== 8'b11111111) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "sel", 0);
            $display("            %s = %h", "a", 8'b11111111);
            $display("            %s = %h", "b", 8'b00000000);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        sel = 1;
        a = 8'b00000000;
        b = 8'b11111111;
        #10;

        // Check output
        if (out !== 8'b11111111) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "sel", 1);
            $display("            %s = %h", "a", 8'b00000000);
            $display("            %s = %h", "b", 8'b11111111);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        sel = 1;
        a = 8'b11111111;
        b = 8'b00000000;
        #10;

        // Check output
        if (out !== 8'b00000000) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "sel", 1);
            $display("            %s = %h", "a", 8'b11111111);
            $display("            %s = %h", "b", 8'b00000000);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        sel = 0;
        a = 8'b10101010;
        b = 8'b01010101;
        #10;

        // Check output
        if (out !== 8'b10101010) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "sel", 0);
            $display("            %s = %h", "a", 8'b10101010);
            $display("            %s = %h", "b", 8'b01010101);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        sel = 1;
        a = 8'b10101010;
        b = 8'b01010101;
        #10;

        // Check output
        if (out !== 8'b01010101) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "sel", 1);
            $display("            %s = %h", "a", 8'b10101010);
            $display("            %s = %h", "b", 8'b01010101);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        sel = 0;
        a = 8'b00001111;
        b = 8'b11110000;
        #10;

        // Check output
        if (out !== 8'b00001111) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "sel", 0);
            $display("            %s = %h", "a", 8'b00001111);
            $display("            %s = %h", "b", 8'b11110000);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        sel = 1;
        a = 8'b00001111;
        b = 8'b11110000;
        #10;

        // Check output
        if (out !== 8'b11110000) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "sel", 1);
            $display("            %s = %h", "a", 8'b00001111);
            $display("            %s = %h", "b", 8'b11110000);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end
    end

    // Final reporting
    final begin
        $display("Simulation finished at %0d ps", $time);
        if (errors == 0)
            $display("TEST PASSED");
        else
            $display("TEST FAILED with %0d errors", errors);
    end

endmodule
