module TopModule (
    input  logic a,
    input  logic b,
    input  logic c,
    input  logic d,
    output logic out_sop,
    output logic out_pos
);

    // out_sop = a'b'cd' + ab'c'd + abc'd' + abcd
    assign out_sop = (~a & ~b & c & ~d) | (a & ~b & ~c & d) | (a & b & ~c & ~d) | (a & b & c & d);

    // out_pos = (a + b + c + d)(a + b + c + ~d)(a + b + ~c + d)(a + ~b + c + d)(~a + b + c + d)
    assign out_pos = (~a | ~b | ~c | ~d) & (~a | ~b | ~c | d) & (~a | ~b | c | ~d) & (~a | b | ~c | ~d) & (a | ~b | ~c | ~d);

endmodule
