`timescale 1ns/1ps

module TopModule_tb;

    logic  out;

    // Stats for tracking errors
      int errors;
    // Instantiate the Device Under Test (DUT)
    TopModule dut (
        .out(out)
    );

    // Test stimulus
    initial begin
        // Initialize inputs

        // Wait for reset
        #10;

        // Apply input stimulus
        #10;

        // Check output
        if (out !== 1'b1) begin
            $display("Time %0t: Inputs", $time);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end
    end

    // Final reporting
    final begin
        $display("Simulation finished at %0d ps", $time);
        if (errors == 0)
            $display("TEST PASSED");
        else
            $display("TEST FAILED with %0d errors", errors);
    end

endmodule
