testbench_results.testbench:
`timescale 1ns/1ps

module TopModule_tb;

    logic  in;
    logic  out;

    // Stats for tracking errors
      int errors;
    // Instantiate the Device Under Test (DUT)
    TopModule dut (
        .in(in),
        .out(out)
    );

    // Test stimulus
    initial begin
        // Initialize inputs
        in = 1'b0;

        // Wait for reset
        #10;

        // Apply input stimulus
        in = 0;
        #10;

        // Check output
        if (out !== 1) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 1;
        #10;

        // Check output
        if (out !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end
    end

    // Final reporting
    final begin
        $display("Simulation finished at %0d ps", $time);
        if (errors == 0)
            $display("TEST PASSED");
        else
            $display("TEST FAILED with %0d errors", errors);
    end

endmodule
