testbench_results.testbench:
`timescale 1ns/1ps

module TopModule_tb;

    logic  a;
    logic  b;
    logic  out_assign;
    logic  out_alwaysblock;

    // Stats for tracking errors
      int errors;
    // Instantiate the Device Under Test (DUT)
    TopModule dut (
        .a(a),
        .b(b),
        .out_assign(out_assign),
        .out_alwaysblock(out_alwaysblock)
    );

    // Test stimulus
    initial begin
        // Initialize inputs
        a = 1'b0;
        b = 1'b0;

        // Wait for reset
        #10;

        // Apply input stimulus
        a = 0;
        b = 0;
        #10;

        // Check output
        if (out_assign !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 0);
            $display("            %s = %h", "b", 0);
            $display("Time %0t: Output %s = %h", $time, "out_assign", out_assign);
            errors++;
        end
        if (out_alwaysblock !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 0);
            $display("            %s = %h", "b", 0);
            $display("Time %0t: Output %s = %h", $time, "out_alwaysblock", out_alwaysblock);
            errors++;
        end

        // Apply input stimulus
        a = 0;
        b = 1;
        #10;

        // Check output
        if (out_assign !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 0);
            $display("            %s = %h", "b", 1);
            $display("Time %0t: Output %s = %h", $time, "out_assign", out_assign);
            errors++;
        end
        if (out_alwaysblock !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 0);
            $display("            %s = %h", "b", 1);
            $display("Time %0t: Output %s = %h", $time, "out_alwaysblock", out_alwaysblock);
            errors++;
        end

        // Apply input stimulus
        a = 1;
        b = 0;
        #10;

        // Check output
        if (out_assign !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 1);
            $display("            %s = %h", "b", 0);
            $display("Time %0t: Output %s = %h", $time, "out_assign", out_assign);
            errors++;
        end
        if (out_alwaysblock !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 1);
            $display("            %s = %h", "b", 0);
            $display("Time %0t: Output %s = %h", $time, "out_alwaysblock", out_alwaysblock);
            errors++;
        end

        // Apply input stimulus
        a = 1;
        b = 1;
        #10;

        // Check output
        if (out_assign !== 1) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 1);
            $display("            %s = %h", "b", 1);
            $display("Time %0t: Output %s = %h", $time, "out_assign", out_assign);
            errors++;
        end
        if (out_alwaysblock !== 1) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 1);
            $display("            %s = %h", "b", 1);
            $display("Time %0t: Output %s = %h", $time, "out_alwaysblock", out_alwaysblock);
            errors++;
        end
    end

    // Final reporting
    final begin
        $display("Simulation finished at %0d ps", $time);
        if (errors == 0)
            $display("TEST PASSED");
        else
            $display("TEST FAILED with %0d errors", errors);
    end

endmodule
