testbench_results.testbench:
`timescale 1ns/1ps

module TopModule_tb;

    logic  in;
    logic [1:0] state;
    logic [1:0] next_state;
    logic  out;

    // Stats for tracking errors
      int errors;
    // Instantiate the Device Under Test (DUT)
    TopModule dut (
        .in(in),
        .state(state),
        .next_state(next_state),
        .out(out)
    );

    // Test stimulus
    initial begin
        // Initialize inputs
        in = 1'b0;
        state = 2'b0;

        // Wait for reset
        #10;

        // Apply input stimulus
        in = 0;
        state = 00;
        #10;

        // Check output
        if (next_state !== 00) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 00);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 00);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 1;
        state = 00;
        #10;

        // Check output
        if (next_state !== 01) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("            %s = %h", "state", 00);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("            %s = %h", "state", 00);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 0;
        state = 01;
        #10;

        // Check output
        if (next_state !== 10) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 01);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 01);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 1;
        state = 01;
        #10;

        // Check output
        if (next_state !== 01) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("            %s = %h", "state", 01);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("            %s = %h", "state", 01);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 0;
        state = 10;
        #10;

        // Check output
        if (next_state !== 00) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 10);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 10);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 1;
        state = 10;
        #10;

        // Check output
        if (next_state !== 11) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("            %s = %h", "state", 10);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("            %s = %h", "state", 10);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 0;
        state = 11;
        #10;

        // Check output
        if (next_state !== 10) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 11);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 1) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 11);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 1;
        state = 11;
        #10;

        // Check output
        if (next_state !== 01) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("            %s = %h", "state", 11);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 1) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("            %s = %h", "state", 11);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end
    end

    // Final reporting
    final begin
        $display("Simulation finished at %0d ps", $time);
        if (errors == 0)
            $display("TEST PASSED");
        else
            $display("TEST FAILED with %0d errors", errors);
    end

endmodule
