module TopModule (
    input logic clk,         // Clock input, triggers on the positive edge
    input logic reset,       // Synchronous active-high reset
    input logic w,           // State transition input
    output logic z           // State-dependent output
);

    typedef enum logic [2:0] {
        A = 3'b000,
        B = 3'b001,
        C = 3'b010,
        D = 3'b011,
        E = 3'b100,
        F = 3'b101
    } state_t;

    state_t current_state, next_state;

    always_ff @(posedge clk) begin
        if (reset) begin
            current_state <= A;
            z <= 0;
        end else begin
            current_state <= next_state;
        end
    end

    always_comb begin
        case (current_state)
            A: begin
                if (w) next_state = B;
                else next_state = A;
                z = 0;
            end
            B: begin
                if (w) next_state = C;
                else next_state = D;
                z = 0;
            end
            C: begin
                if (w) next_state = E;
                else next_state = D;
                z = 0;
            end
            D: begin
                if (w) next_state = F;
                else next_state = A;
                z = 0;
            end
            E: begin
                if (w) next_state = E;
                else next_state = D;
                z = 1;
            end
            F: begin
                if (w) next_state = C;
                else next_state = D;
                z = 1;
            end
            default: begin
                next_state = A;
                z = 0;
            end
        endcase
    end

endmodule
