`timescale 1ns/1ps

module TopModule_tb;

    logic  a;
    logic  b;
    logic  sum;
    logic  cout;

    // Stats for tracking errors
      int errors;
    // Instantiate the Device Under Test (DUT)
    TopModule dut (
        .a(a),
        .b(b),
        .sum(sum),
        .cout(cout)
    );

    // Test stimulus
    initial begin
        // Initialize inputs
        a = 1'b0;
        b = 1'b0;

        // Wait for reset
        #10;

        // Apply input stimulus
        a = 0;
        b = 0;
        #10;

        // Check output
        if (sum !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 0);
            $display("            %s = %h", "b", 0);
            $display("Time %0t: Output %s = %h", $time, "sum", sum);
            errors++;
        end
        if (cout !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 0);
            $display("            %s = %h", "b", 0);
            $display("Time %0t: Output %s = %h", $time, "cout", cout);
            errors++;
        end

        // Apply input stimulus
        a = 0;
        b = 1;
        #10;

        // Check output
