testbench_results.testbench:
`timescale 1ns/1ps

module TopModule_tb;

    logic [7:0] in;
    logic  parity;

    // Stats for tracking errors
      int errors;
    // Instantiate the Device Under Test (DUT)
    TopModule dut (
        .in(in),
        .parity(parity)
    );

    // Test stimulus
    initial begin
        // Initialize inputs
        in = 8'b0;

        // Wait for reset
        #10;

        // Apply input stimulus
        in = 8'b00000000;
        #10;

        // Check output
        if (parity !== 1'b0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 8'b00000000);
            $display("Time %0t: Output %s = %h", $time, "parity", parity);
            errors++;
        end

        // Apply input stimulus
        in = 8'b00000001;
        #10;

        // Check output
        if (parity !== 1'b1) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 8'b00000001);
            $display("Time %0t: Output %s = %h", $time, "parity", parity);
            errors++;
        end

        // Apply input stimulus
        in = 8'b00000011;
        #10;

        // Check output
        if (parity !== 1'b0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 8'b00000011);
            $display("Time %0t: Output %s = %h", $time, "parity", parity);
            errors++;
        end

        // Apply input stimulus
        in = 8'b00000111;
        #10;

        // Check output
        if (parity !== 1'b1) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 8'b00000111);
            $display("Time %0t: Output %s = %h", $time, "parity", parity);
            errors++;
        end

        // Apply input stimulus
        in = 8'b00001111;
        #10;

        // Check output
        if (parity !== 1'b0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 8'b00001111);
            $display("Time %0t: Output %s = %h", $time, "parity", parity);
            errors++;
        end

        // Apply input stimulus
        in = 8'b11111111;
        #10;

        // Check output
        if (parity !== 1'b0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 8'b11111111);
            $display("Time %0t: Output %s = %h", $time, "parity", parity);
            errors++;
        end

        // Apply input stimulus
        in = 8'b10101010;
        #10;

        // Check output
        if (parity !== 1'b0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 8'b10101010);
            $display("Time %0t: Output %s = %h", $time, "parity", parity);
            errors++;
        end

        // Apply input stimulus
        in = 8'b11001100;
        #10;

        // Check output
        if (parity !== 1'b0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 8'b11001100);
            $display("Time %0t: Output %s = %h", $time, "parity", parity);
            errors++;
        end

        // Apply input stimulus
        in = 8'b00110011;
        #10;

        // Check output
        if (parity !== 1'b0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 8'b00110011);
            $display("Time %0t: Output %s = %h", $time, "parity", parity);
