            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 8'b10011001);
            $display("Time %0t: Output %s = %h", $time, "parity", parity);
            errors++;
        end

        // Apply input stimulus
        in = 8'b11100000;
        #10;

        // Check output
        if (parity !== 1'b1) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 8'b11100000);
            $display("Time %0t: Output %s = %h", $time, "parity", parity);
            errors++;
        end

        // Apply input stimulus
        in = 8'b00011110;
        #10;

        // Check output
        if (parity !== 1'b1) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 8'b00011110);
            $display("Time %0t: Output %s = %h", $time, "parity", parity);
