    input logic [2:0] b,
    output logic [2:0] out_or_bitwise,
    output logic out_or_logical,
    output logic [5:0] out_not
);

    // Compute the bitwise OR of a and b
    assign out_or_bitwise = a | b;

    // Compute the logical OR of a and b
    assign out_or_logical = |(a | b);

