`timescale 1ns/1ps

module TopModule_tb;

    logic [7:0] in;
    logic [7:0] out;

    // Stats for tracking errors
      int errors;
    // Instantiate the Device Under Test (DUT)
    TopModule dut (
        .in(in),
        .out(out)
    );

    // Test stimulus
    initial begin
        // Initialize inputs
        in = 8'b0;

        // Wait for reset
        #10;

        // Apply input stimulus
        in = 00000000;
        #10;

        // Check output
        if (out !== 00000000) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 00000000);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 11111111;
        #10;

        // Check output
        if (out !== 11111111) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 11111111);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 10101010;
        #10;

        // Check output
        if (out !== 01010101) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 10101010);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 11001100;
        #10;

        // Check output
        if (out !== 00110011) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 11001100);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 10000001;
        #10;

        // Check output
        if (out !== 10000001) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 10000001);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 00011010;
        #10;

        // Check output
        if (out !== 01011000) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 00011010);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 10100101;
        #10;

        // Check output
        if (out !== 10100101) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 10100101);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 01010101;
        #10;

        // Check output
        if (out !== 10101010) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 01010101);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 11100001;
        #10;

        // Check output
        if (out !== 10000111) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 11100001);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 00111100;
        #10;

        // Check output
        if (out !== 00111100) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 00111100);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end
    end

    // Final reporting
    final begin
        $display("Simulation finished at %0d ps", $time);
        if (errors == 0)
            $display("TEST PASSED");
        else
            $display("TEST FAILED with %0d errors", errors);
    end

endmodule
