`timescale 1ns/1ps

module TopModule_tb;

    logic [2:0] in;
    logic [1:0] out;

    // Stats for tracking errors
      int errors;
    // Instantiate the Device Under Test (DUT)
    TopModule dut (
        .in(in),
        .out(out)
    );

    // Test stimulus
    initial begin
        // Initialize inputs
        in = 3'b0;

        // Wait for reset
        #10;

        // Apply input stimulus
        in = 3'b000;
        #10;

        // Check output
        if (out !== 2'b00) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 3'b000);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 3'b001;
        #10;

        // Check output
        if (out !== 2'b01) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 3'b001);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 3'b010;
        #10;

        // Check output
        if (out !== 2'b01) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 3'b010);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 3'b011;
        #10;

        // Check output
        if (out !== 2'b10) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 3'b011);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 3'b100;
        #10;

        // Check output
        if (out !== 2'b01) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 3'b100);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 3'b101;
        #10;

        // Check output
        if (out !== 2'b10) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 3'b101);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 3'b110;
        #10;

        // Check output
        if (out !== 2'b10) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 3'b110);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 3'b111;
        #10;

        // Check output
        if (out !== 2'b11) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 3'b111);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end
    end

    // Final reporting
    final begin
        $display("Simulation finished at %0d ps", $time);
        if (errors == 0)
            $display("TEST PASSED");
        else
            $display("TEST FAILED with %0d errors", errors);
    end

endmodule
