`timescale 1ns/1ps

module TopModule_tb;

    logic [99:0] a;
    logic [99:0] b;
    logic  sel;
    logic [99:0] out;

    // Stats for tracking errors
      int errors;
    // Instantiate the Device Under Test (DUT)
    TopModule dut (
        .a(a),
        .b(b),
        .sel(sel),
        .out(out)
    );

    // Test stimulus
    initial begin
        // Initialize inputs
        a = 100'b0;
        b = 100'b0;
        sel = 1'b0;

        // Wait for reset
        #10;

        // Apply input stimulus
        a = 100'b0;
        b = 100'b0;
        sel = 1'b0;
        #10;

        // Check output
        if (out !== 100'b0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 100'b0);
            $display("            %s = %h", "b", 100'b0);
            $display("            %s = %h", "sel", 1'b0);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        a = 100'hFFFFFFFFFFFFFFFFFFFFFFFFF;
        b = 100'b0;
        sel = 1'b0;
        #10;

        // Check output
        if (out !== 100'hFFFFFFFFFFFFFFFFFFFFFFFFF) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 100'hFFFFFFFFFFFFFFFFFFFFFFFFF);
            $display("            %s = %h", "b", 100'b0);
            $display("            %s = %h", "sel", 1'b0);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        a = 100'b0;
        b = 100'hFFFFFFFFFFFFFFFFFFFFFFFFF;
        sel = 1'b1;
        #10;

        // Check output
        if (out !== 100'hFFFFFFFFFFFFFFFFFFFFFFFFF) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 100'b0);
            $display("            %s = %h", "b", 100'hFFFFFFFFFFFFFFFFFFFFFFFFF);
            $display("            %s = %h", "sel", 1'b1);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        a = 100'hAAAAAAAAAAAAAAAAAAAAAAAAA;
        b = 100'h5555555555555555555555555;
        sel = 1'b0;
        #10;

        // Check output
        if (out !== 100'hAAAAAAAAAAAAAAAAAAAAAAAAA) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 100'hAAAAAAAAAAAAAAAAAAAAAAAAA);
            $display("            %s = %h", "b", 100'h5555555555555555555555555);
            $display("            %s = %h", "sel", 1'b0);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        a = 100'hAAAAAAAAAAAAAAAAAAAAAAAAA;
        b = 100'h5555555555555555555555555;
        sel = 1'b1;
        #10;

        // Check output
        if (out !== 100'h5555555555555555555555555) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 100'hAAAAAAAAAAAAAAAAAAAAAAAAA);
            $display("            %s = %h", "b", 100'h5555555555555555555555555);
            $display("            %s = %h", "sel", 1'b1);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        a = 100'h0123456789ABCDEF0123456789;
        b = 100'hFEDCBA9876543210FEDCBA9876;
        sel = 1'b0;
        #10;

        // Check output
        if (out !== 100'h0123456789ABCDEF0123456789) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 100'h0123456789ABCDEF0123456789);
            $display("            %s = %h", "b", 100'hFEDCBA9876543210FEDCBA9876);
            $display("            %s = %h", "sel", 1'b0);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        a = 100'h0123456789ABCDEF0123456789;
        b = 100'hFEDCBA9876543210FEDCBA9876;
        sel = 1'b1;
        #10;

        // Check output
        if (out !== 100'hFEDCBA9876543210FEDCBA9876) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "a", 100'h0123456789ABCDEF0123456789);
            $display("            %s = %h", "b", 100'hFEDCBA9876543210FEDCBA9876);
            $display("            %s = %h", "sel", 1'b1);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end
    end

    // Final reporting
    final begin
        $display("Simulation finished at %0d ps", $time);
        if (errors == 0)
            $display("TEST PASSED");
        else
            $display("TEST FAILED with %0d errors", errors);
    end

endmodule
