testbench_results.testbench:
`timescale 1ns/1ps

module TopModule_tb;

    logic  in;
    logic [3:0] state;
    logic [3:0] next_state;
    logic  out;

    // Stats for tracking errors
      int errors;
    // Instantiate the Device Under Test (DUT)
    TopModule dut (
        .in(in),
        .state(state),
        .next_state(next_state),
        .out(out)
    );

    // Test stimulus
    initial begin
        // Initialize inputs
        in = 1'b0;
        state = 4'b0;

        // Wait for reset
        #10;

        // Apply input stimulus
        in = 0;
        state = 0001;
        #10;

        // Check output
        if (next_state !== 0001) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 0001);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 0001);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 1;
        state = 0001;
        #10;

        // Check output
        if (next_state !== 0010) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("            %s = %h", "state", 0001);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("            %s = %h", "state", 0001);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 0;
        state = 0010;
        #10;

        // Check output
        if (next_state !== 0100) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 0010);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 0010);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 1;
        state = 0010;
        #10;

        // Check output
        if (next_state !== 0010) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("            %s = %h", "state", 0010);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("            %s = %h", "state", 0010);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 0;
        state = 0100;
        #10;

        // Check output
        if (next_state !== 0001) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 0100);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 0100);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 1;
        state = 0100;
        #10;

        // Check output
        if (next_state !== 1000) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("            %s = %h", "state", 0100);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("            %s = %h", "state", 0100);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 0;
        state = 1000;
        #10;

        // Check output
        if (next_state !== 0100) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 1000);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 1) begin
