            $display("            %s = %h", "state", 1000);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 0;
        state = 0000;
        #10;

        // Check output
        if (next_state !== 0000) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 0000);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 0);
            $display("            %s = %h", "state", 0000);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end

        // Apply input stimulus
        in = 1;
        state = 0000;
        #10;

        // Check output
        if (next_state !== 0000) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("            %s = %h", "state", 0000);
            $display("Time %0t: Output %s = %h", $time, "next_state", next_state);
            errors++;
        end
        if (out !== 0) begin
            $display("Time %0t: Inputs", $time);
            $display("            %s = %h", "in", 1);
            $display("            %s = %h", "state", 0000);
            $display("Time %0t: Output %s = %h", $time, "out", out);
            errors++;
        end
    end

    // Final reporting
    final begin
        $display("Simulation finished at %0d ps", $time);
        if (errors == 0)
            $display("TEST PASSED");
        else
            $display("TEST FAILED with %0d errors", errors);
    end

endmodule
