module TopModule(
    input logic x3,
    input logic x2,
    input logic x1,
    output logic f
);

always @(*) begin
    case ({x3, x2, x1})
        3'b000: f = 1'b0;
        3'b001: f = 1'b0;
        3'b010: f = 1'b1;
        3'b011: f = 1'b1;
        3'b100: f = 1'b0;
        3'b101: f = 1'b1;
        3'b110: f = 1'b0;
        3'b111: f = 1'b1;
        default: f = 1'b0;
    endcase
end

endmodule
